import React, { useState, useEffect } from 'react';
import './CalendarPopup.css';

const LOCAL_STORAGE_KEY = 'calendarMemos'; // Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ ÌÇ§ ÏÉÅÏàò

const CalendarPopup = ({ onClose }) => {
  // üìÖ ÏÉÅÌÉú Ï†ïÏùò: ÎÇ†Ïßú, Î©îÎ™® ÎÇ¥Ïö©, Ï†ÄÏû• Î™©Î°ù, ÏàòÏ†ï Ïù∏Îç±Ïä§
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [memo, setMemo] = useState('');
  const [savedMemos, setSavedMemos] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null); // ÏàòÏ†ï Ï§ëÏù∏ÏßÄ Ïó¨Î∂Ä

  // üìÇ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Î©îÎ™® Î∂àÎü¨Ïò§Í∏∞ (Ï¥àÍ∏∞ Î†åÎçîÎßÅ Ïãú)
  useEffect(() => {
    const stored = localStorage.getItem(LOCAL_STORAGE_KEY);
    if (stored) {
      try {
        setSavedMemos(JSON.parse(stored));
      } catch (err) {
        console.error('Î©îÎ™® Î∂àÎü¨Ïò§Í∏∞ Ïò§Î•ò:', err);
      }
    }
  }, []);

  // ‚úÖ ÏöîÏùºÎ≥Ñ ÏÉâÏÉÅ Î∞òÌôò Ìï®Ïàò (Ïùº: Îπ®Í∞ï, ÌÜ†: ÌååÎûë, ÌèâÏùº: Í≤ÄÏ†ï)
  const getDayColor = (dateStr) => {
    if (!dateStr) return 'inherit';
    const day = new Date(dateStr).getDay();
    return day === 0 ? 'red' : day === 6 ? 'blue' : 'black';
  };

  // üîÑ ÏûÖÎ†•Í∞í Ï¥àÍ∏∞Ìôî Ìï®Ïàò
  const resetForm = () => {
    setStartDate('');
    setEndDate('');
    setMemo('');
    setEditingIndex(null);
  };

  // üíæ Î©îÎ™® Ï†ÄÏû• ÎòêÎäî ÏàòÏ†ï Ï≤òÎ¶¨
  const handleSaveMemo = () => {
    // ÌïÑÏàò Ìï≠Î™©Ïù¥ Îπ†Ï°åÏúºÎ©¥ Ï†ÄÏû• Ï§ëÎã®
    if (!startDate || !endDate || !memo.trim()) return;

    // üõë Ï¢ÖÎ£åÏùºÏù¥ ÏãúÏûëÏùºÎ≥¥Îã§ Îπ†Î•¥Î©¥ Í≤ΩÍ≥† ÌõÑ Ï§ëÎã®
    if (new Date(startDate) > new Date(endDate)) {
      alert('‚õî Ï¢ÖÎ£åÏùºÏùÄ ÏãúÏûëÏùº Ïù¥ÌõÑÏó¨Ïïº Ìï©ÎãàÎã§.');
      return;
    }

    const newMemo = { startDate, endDate, content: memo };
    let updated;

    if (editingIndex !== null) {
      // ‚úèÔ∏è Í∏∞Ï°¥ Î©îÎ™® ÏàòÏ†ï Ï§ëÏù¥Î©¥ Ìï¥Îãπ Ìï≠Î™© ÏóÖÎç∞Ïù¥Ìä∏
      updated = [...savedMemos];
      updated[editingIndex] = newMemo;
    } else {
      // üîç Ï§ëÎ≥µÎêú ÎÇ†Ïßú Î≤îÏúÑ ÌôïÏù∏
      const existingIndex = savedMemos.findIndex(
        (m) => m.startDate === startDate && m.endDate === endDate
      );

      if (existingIndex !== -1) {
        // ‚ö†Ô∏è Ï§ëÎ≥µÎêú Ìï≠Î™©Ïù¥ ÏûàÏúºÎ©¥ ÏÇ¨Ïö©ÏûêÏóêÍ≤å ÎçÆÏñ¥Ïì∞Í∏∞ Ïó¨Î∂Ä ÌôïÏù∏
        const confirmOverwrite = window.confirm(
          'Ïù¥ÎØ∏ Í∞ôÏùÄ ÎÇ†Ïßú Î≤îÏúÑÏùò Î©îÎ™®Í∞Ä Ï°¥Ïû¨Ìï©ÎãàÎã§. ÎçÆÏñ¥Ïì∏ÍπåÏöî?'
        );
        if (!confirmOverwrite) return;

        updated = [...savedMemos];
        updated[existingIndex] = newMemo;
      } else {
        // ÏÉà Ìï≠Î™© Ï∂îÍ∞Ä
        updated = [...savedMemos, newMemo];
      }
    }

    // ÏÉÅÌÉú Î∞è Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ ÎèôÍ∏∞Ìôî
    setSavedMemos(updated);
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(updated));

    // ÏûÖÎ†• Ï¥àÍ∏∞Ìôî
    resetForm();
  };

  // üóëÔ∏è Î©îÎ™® ÏÇ≠Ï†ú Ï≤òÎ¶¨
  const handleDeleteMemo = (index) => {
    const updated = [...savedMemos];
    updated.splice(index, 1); // Ìï¥Îãπ Ïù∏Îç±Ïä§ ÏÇ≠Ï†ú
    setSavedMemos(updated);
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(updated));
    resetForm(); // ÌòπÏãú ÏàòÏ†ï Ï§ëÏù¥ÏóàÏùÑ Í≤ΩÏö∞ Ï¥àÍ∏∞Ìôî
  };

  // ‚úèÔ∏è Ï†ÄÏû•Îêú Î©îÎ™® ÌÅ¥Î¶≠ Ïãú ÏàòÏ†ï Í∞ÄÎä•ÌïòÍ≤å ÏÑ∏ÌåÖ
  const handleSelectMemo = (memoObj, index) => {
    setStartDate(memoObj.startDate);
    setEndDate(memoObj.endDate);
    setMemo(memoObj.content);
    setEditingIndex(index); // ÏàòÏ†ï Ï§ë ÌëúÏãú
  };

  return (
    <div className="calendar-box" role="dialog" aria-modal="true" aria-label="Ï∫òÎ¶∞Îçî Î©îÎ™® ÌåùÏóÖ">
      {/* üìÜ ÎÇ†Ïßú ÏûÖÎ†• ÌïÑÎìú */}
      <div className="date-range-picker">
        <label>
          ÏãúÏûëÏùº:
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            style={{ color: getDayColor(startDate) }}
            aria-label="ÏãúÏûëÏùº ÏÑ†ÌÉù"
          />
        </label>

        <label>
          Ï¢ÖÎ£åÏùº:
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            style={{ color: getDayColor(endDate) }}
            aria-label="Ï¢ÖÎ£åÏùº ÏÑ†ÌÉù"
          />
        </label>
      </div>

      {/* üìù Î©îÎ™® ÏûÖÎ†• ÌïÑÎìú */}
      {startDate && endDate && (
        <div className="memo-box">
          <textarea
            value={memo}
            onChange={(e) => setMemo(e.target.value)}
            placeholder="Î©îÎ™®Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            aria-label="Î©îÎ™® ÏûÖÎ†•"
          />
          <div className="memo-buttons">
            <button onClick={handleSaveMemo}>
              {editingIndex !== null ? '‚úè ÏàòÏ†ï' : 'üíæ Ï†ÄÏû•'}
            </button>
            <button onClick={resetForm}>üßπ Ï¥àÍ∏∞Ìôî</button>
            <button onClick={onClose}>‚ùå Îã´Í∏∞</button>
          </div>
        </div>
      )}

      {/* üìã Ï†ÄÏû•Îêú Î©îÎ™® Î™©Î°ù */}
      {savedMemos.length > 0 && (
        <div className="saved-memos-list">
          <h4>Ï†ÄÏû•Îêú Î™©Î°ù</h4>
          <ul>
            {savedMemos.map((memoObj, index) => (
              <li
                key={`${memoObj.startDate}-${memoObj.endDate}`}
                onClick={() => handleSelectMemo(memoObj, index)}
                tabIndex="0"
              >
                {/* ÎÇ†Ïßú Î≤îÏúÑ ÌëúÏãú */}
                <div className="memo-date">
                  <span style={{ color: getDayColor(memoObj.startDate) }}>{memoObj.startDate}</span>
                  {' ~ '}
                  <span style={{ color: getDayColor(memoObj.endDate) }}>{memoObj.endDate}</span>
                </div>

                {/* Î©îÎ™® ÎÇ¥Ïö© ÌëúÏãú */}
                <div className="memo-preview">{memoObj.content}</div>

                {/* ÏÇ≠Ï†ú Î≤ÑÌäº */}
                <button
                  className="delete-btn"
                  onClick={(e) => {
                    e.stopPropagation(); // Î©îÎ™® ÏÑ†ÌÉù Ïù¥Î≤§Ìä∏ ÎßâÍ∏∞
                    handleDeleteMemo(index);
                  }}
                  aria-label="Î©îÎ™® ÏÇ≠Ï†ú"
                >
                  ÏÇ≠Ï†ú
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CalendarPopup;
