import React, { useState } from 'react';

const Sidebar = ({
  onSearch,
  showEmoji,
  setShowEmoji,
  darkMode,
  setDarkMode,
  searchQuery, setSearchQuery
}) => {
  const [inputValue, setInputValue] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);

  // ‚å®Ô∏è Í≤ÄÏÉâ Ïã§Ìñâ Ìï®Ïàò
  const handleSearch = () => {
    const trimmedInput = inputValue.trim();
    if (trimmedInput === '') return;

    // AppÏúºÎ°ú Í≤ÄÏÉâÏñ¥ Ï†ÑÎã¨
    setSearchQuery(trimmedInput);

    // üß† Ï§ëÎ≥µ Ï†úÍ±∞ + ÏµúÍ∑º Ìï≠Î™© Ï∂îÍ∞Ä
     setSearchHistory((prev) => {
      const filtered = prev.filter((item) => item !== trimmedInput);
      return [trimmedInput, ...filtered].slice(0, 5);
    });

  setInputValue(''); // ÏûÖÎ†•Ï∞Ω Ï¥àÍ∏∞Ìôî
};

// üóëÔ∏è Í≤ÄÏÉâ ÌûàÏä§ÌÜ†Î¶¨ÏóêÏÑú Í∞úÎ≥Ñ ÌÇ§ÏõåÎìú ÏÇ≠Ï†ú
const handleDeleteKeyword = (keyword) => {
  setSearchHistory(prev => prev.filter((item) => item !== keyword));
};

return (
  <div className={`sidebar ${darkMode ? 'dark' : ''}`}>
    <h2>üõ†Ô∏è Í∏∞Îä• Î©îÎâ¥</h2>

    {/* üîç ÏúÑÏπò Í≤ÄÏÉâ */}
    <h3>ÏúÑÏπò Í≤ÄÏÉâ</h3>
    <input
      type="text"
      className="search-input"
      placeholder="ÎèÑÏãúÎÇò ÏßÄÏó≠ Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
      value={inputValue}
      onChange={(e) => setInputValue(e.target.value)}
      onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
    />
    <button className="search-button" onClick={handleSearch}>üîç Í≤ÄÏÉâ</button>

    {/* üìú ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ ÌëúÏãú */}
    {searchHistory.length > 0 && (
      <>
        <h4>ÏµúÍ∑º Í≤ÄÏÉâÏñ¥</h4>
        <ul className="search-history">
          {searchHistory.map((item, index) => (
            <li
              key={index}
              style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '4px'
              }}
            >
              <span
                style={{ cursor: 'pointer' }}
                onClick={() => {
                  setInputValue(item);
                  onSearch(item);     // ÌûàÏä§ÌÜ†Î¶¨ ÌÅ¥Î¶≠ ÏãúÏóêÎèÑ onSearch Îßå Ìò∏Ï∂ú
                }}
              >
                - {item}
              </span>
              <button
                onClick={() => handleDeleteKeyword(item)} // Ìï¥Îãπ Í≤ÄÏÉâÏñ¥ ÏÇ≠Ï†ú
                style={{
                  marginLeft: '10px',
                  backgroundColor: 'transparent',
                  border: 'none',
                  color: 'red',
                  cursor: 'pointer'
                }}
                title="ÏÇ≠Ï†ú"
              >
                ‚ùå
              </button>
            </li>
          ))}
        </ul>
        {/* üîÑ Ï†ÑÏ≤¥ Í≤ÄÏÉâÏñ¥ Í∏∞Î°ù ÏÇ≠Ï†ú */}
        <button
          onClick={() => setSearchHistory([])}
          style={{ marginTop: '10px' }}
        >
          üßπ Ï†ÑÏ≤¥ Í∏∞Î°ù ÏÇ≠Ï†ú
        </button>
      </>
    )}

    <hr />

    {/* üåô Îã§ÌÅ¨ Î™®Îìú */}
    <h3>Îã§ÌÅ¨ Î™®Îìú</h3>
    <button onClick={() => setDarkMode(!darkMode)}>
      {darkMode ? 'üí° ÎùºÏù¥Ìä∏ Î™®Îìú' : 'üåô Îã§ÌÅ¨ Î™®Îìú'}
    </button>

    {/* üîÑ Ï¥àÍ∏∞Ìôî */}
    <h3>Ï¥àÍ∏∞Ìôî</h3>
    <button onClick={() => window.location.reload()}>üîÑ Ï¥àÍ∏∞Ìôî</button>
  </div>
);
};

export default Sidebar;
