import React, { useEffect, useRef, useState } from 'react';

const NAVER_CLIENT_ID = process.env.REACT_APP_NAVER_ID;

function NaverMap({ searchQuery }) {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markerRef = useRef(null);
  const [isMapReady, setIsMapReady] = useState(false);

  // Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú ÌõÑ mapOptionsÎ•º ÏïàÏ†ÑÌïòÍ≤å ÏÇ¨Ïö©
  const loadNaverScript = () => {
    return new Promise((resolve, reject) => {
      if (window.naver && window.naver.maps) {
        resolve();
        return;
      }

      const script = document.createElement('script');
      script.src = `https://oapi.map.naver.com/openapi/v3/maps.js?ncpKeyId=${NAVER_CLIENT_ID}&submodules=geocoder`;
      script.async = true;
      script.defer = true;
      script.onload = () => {
        if (window.naver && window.naver.maps) {
          resolve();
        } else {
          reject(new Error('ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎî© Ïã§Ìå®'));
        }
      };
      script.onerror = reject;

      document.head.appendChild(script);
    });
  };

  // ÏµúÏ¥à ÏßÄÎèÑ Î°úÎî©
  useEffect(() => {
    loadNaverScript().then(() => {
      const mapOptions = {
            center: new window.naver.maps.LatLng(37.5665, 126.978),
            zoom: 14,
            zoomControl: true,
            mapTypeControl: true,
            mapTypeId: window.naver.maps.MapTypeId.NORMAL,
            scaleControl: true,
            logoControl: true,
            padding: { top: 10, right: 10, bottom: 10, left: 10 },
            mapDataControl: false,
            zoomControlOptions: {
              position: window.naver.maps.Position.BOTTOM_LEFT,
              style: 2,
            },
          };


      if (mapRef.current) {
        mapInstance.current = new window.naver.maps.Map(mapRef.current, mapOptions);

        // ÌòÑÏû¨ ÏúÑÏπò
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition((pos) => {
            const currPos = new window.naver.maps.LatLng(
              pos.coords.latitude,
              pos.coords.longitude
            );
            mapInstance.current.setCenter(currPos);
            mapInstance.current.setZoom(13);
            new window.naver.maps.Marker({
              position: currPos,
              map: mapInstance.current,
            });
          });
        }

        setIsMapReady(true);
      }
    }).catch((err) => {
      console.error('ÏßÄÎèÑ Î°úÎìú Ïã§Ìå®:', err);
    });
  }, []);

  // Ï£ºÏÜå Í≤ÄÏÉâ Ïãú ÏßÄÎèÑ Ïù¥Îèô + ÏΩòÏÜî Ï∂úÎ†•
  useEffect(() => {
    if (!searchQuery || !isMapReady || !window.naver?.maps?.Service) return;

    window.naver.maps.Service.geocode(
      { query: searchQuery },
      (status, response) => {
        if (status !== window.naver.maps.Service.Status.OK) {
          alert('Ï£ºÏÜå Í≤ÄÏÉâ Ïã§Ìå®');
          return;
        }

        const result = response.v2.addresses[0];
        if (!result) {
          alert('Í≤ÄÏÉâ Í≤∞Í≥º ÏóÜÏùå');
          return;
        }

        const lat = parseFloat(result.y);
        const lng = parseFloat(result.x);
        const location = new window.naver.maps.LatLng(lat, lng);

        mapInstance.current.setCenter(location);
        mapInstance.current.setZoom(14);

        // ÎßàÏª§ Ï¥àÍ∏∞Ìôî Î∞è Îã§Ïãú Í∑∏Î¶¨Í∏∞
        if (markerRef.current) {
          markerRef.current.setMap(null);
        }
        markerRef.current = new window.naver.maps.Marker({
          position: location,
          map: mapInstance.current,
        });

        // ÏΩòÏÜî Ï∂úÎ†•
        console.log('üîé ÎèÑÎ°úÎ™Ö:', result.roadAddress);
        console.log('ÏßÄÎ≤à:', result.jibunAddress);
        console.log('ÏúÑÎèÑ:', lat, 'Í≤ΩÎèÑ:', lng);
      }
    );
  }, [searchQuery, isMapReady]);

  return <div className="map-container" ref={mapRef} />;
}

export default NaverMap;
