import React, { useEffect, useState, useRef } from 'react';
import './Navbar.css';
import CalendarPopup from './CalendarPopup/CalendarPopup'; // üì¶ Îã¨Î†• + Î©îÎ™® ÌåùÏóÖ Ïª¥Ìè¨ÎÑåÌä∏

// üîß ÏÉÅÎã® ÎÇ¥ÎπÑÍ≤åÏù¥ÏÖò Î∞î Ïª¥Ìè¨ÎÑåÌä∏
const Navbar = ({ darkMode }) => {
  // ‚è∞ ÌòÑÏû¨ ÏãúÍ∞Ñ ÏÉÅÌÉú Ï†ÄÏû•
  const [time, setTime] = useState(new Date());

  // üìÖ Îã¨Î†• ÌåùÏóÖ ÌëúÏãú Ïó¨Î∂Ä
  const [showCalendar, setShowCalendar] = useState(false);

  // üß≠ ÌåùÏóÖ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄÎ•º ÏúÑÌïú ref
  const calendarRef = useRef(null);

  // üïí Ïã§ÏãúÍ∞Ñ ÏãúÍ∞Ñ ÏóÖÎç∞Ïù¥Ìä∏ (1Ï¥àÎßàÎã§ Í∞±Ïã†)
  useEffect(() => {
    const timer = setInterval(() => setTime(new Date()), 1000);
    return () => clearInterval(timer); // üí® Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú ÌÉÄÏù¥Î®∏ Ï†ïÎ¶¨
  }, []);

  // üìÜ ÌòÑÏû¨ ÎÇ†Ïßú Ìè¨Îß∑: Ïòà) 2025ÎÖÑ 7Ïõî 16Ïùº ÏàòÏöîÏùº
  const formattedDate = time.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long',
  });

  // ‚åö ÌòÑÏû¨ ÏãúÍ∞Ñ Ìè¨Îß∑: Ïòà) 11:07:44
  const formattedTime = time.toLocaleTimeString();

  // ‚ùå ÌåùÏóÖ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Ïãú ÏûêÎèô Îã´Í∏∞
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (calendarRef.current && !calendarRef.current.contains(e.target)) {
        setShowCalendar(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <nav className={`navbar ${darkMode ? 'nav-dark' : 'nav-light'}`}>
      {/* ‚¨ÖÔ∏è ÏôºÏ™Ω: Î°úÍ≥† */}
      <div className="logo">üó∫Ô∏è My Map</div>

      {/* ‚û°Ô∏è Ïò§Î•∏Ï™Ω: ÎÇ†Ïßú + ÏãúÍ∞Ñ + Îã¨Î†• ÌåùÏóÖ */}
      <div className="right-section">
        {/* üìÖ ÎÇ†Ïßú ÌÅ¥Î¶≠ Ïãú Îã¨Î†• ÌåùÏóÖ Ïó¥Í∏∞/Îã´Í∏∞ */}
        <div className="date" onClick={() => setShowCalendar((prev) => !prev)}>
          üìÖ {formattedDate}
        </div>

        {/* ‚è∞ ÌòÑÏû¨ ÏãúÍ∞Ñ */}
        <div className="clock">‚è∞ {formattedTime}</div>

        {/* üìå Îã¨Î†• + Î©îÎ™® ÌåùÏóÖ (ÌÅ¥Î¶≠ Ïãú ÎÖ∏Ï∂ú) */}
        {showCalendar && (
          <div ref={calendarRef} className="calendar-popup-wrapper">
            <CalendarPopup onClose={() => setShowCalendar(false)} />
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
