  const formattedDate = time.toLocaleDateString('ko-KR', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long',
  });

  return (
    <nav className={`navbar ${darkMode ? 'nav-dark' : 'nav-light'}`}>
      {/* ÏôºÏ™Ω Î°úÍ≥† ÏòÅÏó≠ */}
      <div className="logo">üó∫Ô∏è ÎÇ¥ ÏßÄÎèÑ Ïï±</div>

