      <div className="right-section">
        {/* Îã¨Î†• ÎÇ†Ïßú ÌëúÏãú */}
        <div className="date">{formattedDate}</div>

        {/* ÏãúÍ≥Ñ */}
        <div className="clock">{formattedTime}</div>
