import React, { useEffect, useRef, useState } from 'react';

const NAVER_CLIENT_ID = process.env.REACT_APP_NAVER_ID;

function NaverMap({ searchQuery }) {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const markerRef = useRef(null);
  const initMarkerRef = useRef(null);
  const [isMapReady, setIsMapReady] = useState(false);
  const [hasSearchedOnce, setHasSearchedOnce] = useState(false);

  // ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎìú
  const loadNaverScript = () => {
    return new Promise((resolve, reject) => {
      if (window.naver && window.naver.maps) {
        resolve();
        return;
      }

      const script = document.createElement('script');
      script.src = `https://oapi.map.naver.com/openapi/v3/maps.js?ncpKeyId=${NAVER_CLIENT_ID}&submodules=geocoder`;
      script.async = true;
      script.defer = true;
      script.onload = () => {
        if (window.naver && window.naver.maps) {
          resolve();
        } else {
          reject(new Error('ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎî© Ïã§Ìå®'));
        }
      };
      script.onerror = reject;

      document.head.appendChild(script);
    });
  };

  // ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    loadNaverScript()
      .then(() => {
        const mapOptions = {
          center: new window.naver.maps.LatLng(37.5665, 126.978),
          zoom: 14,
          zoomControl: true,
          mapTypeControl: true,
          mapTypeId: window.naver.maps.MapTypeId.NORMAL,
          scaleControl: true,
          logoControl: true,
          padding: { top: 10, right: 10, bottom: 10, left: 10 },
          mapDataControl: false,
          zoomControlOptions: {
            position: window.naver.maps.Position.BOTTOM_LEFT,
            style: 2,
          },
        };

        if (mapRef.current) {
          mapInstance.current = new window.naver.maps.Map(mapRef.current, mapOptions);

          if (navigator.geolocation && !hasSearchedOnce) {
            navigator.geolocation.getCurrentPosition((pos) => {
              const currPos = new window.naver.maps.LatLng(pos.coords.latitude, pos.coords.longitude);
              mapInstance.current.setCenter(currPos);
              mapInstance.current.setZoom(13);
              initMarkerRef.current = new window.naver.maps.Marker({
                position: currPos,
                map: mapInstance.current,
              });
            });
          }

          setIsMapReady(true);
        }
      })
      .catch((err) => {
        console.error('ÏßÄÎèÑ Î°úÎìú Ïã§Ìå®:', err);
      });
  }, [hasSearchedOnce]);

  // Ï£ºÏÜå Í≤ÄÏÉâ ‚Üí ÏßÄÎèÑ Ïù¥Îèô
  useEffect(() => {
    if (!searchQuery || !isMapReady) return;

    setHasSearchedOnce(true);

    // Ï£ºÏÜå Ìè¨Îß∑ Ï†ïÏ†ú
    let refinedAddress = searchQuery
      .trim()
      .replace("ÏÑúÏö∏Ïãú", "ÏÑúÏö∏")
      .replace("Í≤ΩÍ∏∞ÎèÑ", "Í≤ΩÍ∏∞")
      .replace("Î∂ÄÏÇ∞Ïãú", "Î∂ÄÏÇ∞")
      .replace(/\s+/g, " ");

    console.log("Ï†ïÏ†úÎêú Ï£ºÏÜå:", refinedAddress);

    // ÌîÑÎ°ùÏãú ÏÑúÎ≤ÑÎ•º ÌÜµÌï¥ Kakao API Ìò∏Ï∂ú
    fetch(`http://localhost:4000/kakao/address?query=${encodeURIComponent(refinedAddress)}`)
      .then((res) => res.json())
      .then((data) => {
        console.log("üì¶ Kakao ÏùëÎãµ:", data);  // ‚úÖ Ïù¥ Ï§Ñ Ï∂îÍ∞ÄÎê®

        if (!data.documents || data.documents.length === 0) {
          alert("Ï£ºÏÜå Í≤ÄÏÉâ Ïã§Ìå®: Ìï¥Îãπ Ï£ºÏÜåÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
          return;
        }

        const { x, y } = data.documents[0]; // x: Í≤ΩÎèÑ, y: ÏúÑÎèÑ
        const location = new window.naver.maps.LatLng(y, x);

        mapInstance.current.setCenter(location);
        mapInstance.current.setZoom(16);

        if (markerRef.current) {
          markerRef.current.setMap(null);
        }

        markerRef.current = new window.naver.maps.Marker({
          position: location,
          map: mapInstance.current,
        });
      })
      .catch((err) => {
        console.error("Kakao Ï£ºÏÜå Í≤ÄÏÉâ Ïò§Î•ò:", err);
        alert("Ï£ºÏÜå Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù");
      });
  }, [searchQuery, isMapReady]);

  return <div ref={mapRef} style={{ width: '100%', height: '100%' }} />;
}

export default NaverMap;
