import React, { useState, useRef, useEffect } from 'react';
import './Sidebar.css';
import { naverLocalSearch, naverGeocode } from '../utils/naverApi';

const Sidebar = ({
  searchResults, setSearchResults,
  selectedPlace, setSelectedPlace,
  onSearch,
  showEmoji, setShowEmoji,
  darkMode, setDarkMode,
  searchQuery,setSearchQuery
}) => {

  // ÏûÖÎ†•Í∞í ÏÉÅÌÉú
  const [inputValue, setInputValue] = useState('');

   // ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú (localStorage Ï¥àÍ∏∞Ìôî)
  const [searchHistory, setSearchHistory] = useState(() => {
    const saved = localStorage.getItem('searchHistory');
    return saved ? JSON.parse(saved) : [];
  });

  // Ï¶êÍ≤®Ï∞æÍ∏∞ Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú
  const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem('favorites');
    return saved ? JSON.parse(saved) : [];
  });

  // ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ ÌÜ†Í∏Ä ÏÉÅÌÉú
  const [showHistory, setShowHistory] = useState(true);

  // Ïò§Î•ò Î©îÏãúÏßÄ ÏÉÅÌÉú
  const [errorMessage, setErrorMessage] = useState('');

  // ÏÇ¨Ïù¥ÎìúÎ∞î Ï†ëÌûò ÏÉÅÌÉú
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // ÏûêÎèôÏôÑÏÑ± Î¶¨Ïä§Ìä∏ ÏÉÅÌÉú
  const [autocompleteList, setAutocompleteList] = useState([]);

  // ÏûêÎèôÏôÑÏÑ± ÏòÅÏó≠ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄÎ•º ÏúÑÌïú ref
  const autocompleteRef = useRef(null);

  // * Ïã†Í∑ú) Îßµ Í≤ÄÏÉâ Î°úÎî©ÏÉÅÌÉú - ÎÑ§Ïù¥Î≤Ñ Í≤ÄÏÉâ APIÍ∞Ä Ï∂îÍ∞ÄÎêêÏäµÎãàÎã§.
  const [loading, setLoading] = useState(false);

  // ÏûÖÎ†•Í∞í Î≥ÄÍ≤Ω Ïãú ÏûêÎèôÏôÑÏÑ± ÏóÖÎç∞Ïù¥Ìä∏
  const handleInputChange = (e) => {
    const val = e.target.value;
    setInputValue(val);

    if (val.length === 0) {
      setAutocompleteList([]);
      setErrorMessage('');
      return;
    }

    const filtered = searchHistory.filter(item =>
      item.toLowerCase().includes(val.toLowerCase())
    );
    setAutocompleteList(filtered.slice(0, 5));
    setErrorMessage('');
  };

  // Í≤ÄÏÉâ Ïã§Ìñâ Ìï®Ïàò: ÌûàÏä§ÌÜ†Î¶¨, ÏóêÎü¨Í¥ÄÎ¶¨, ÏøºÎ¶¨(Í≤ÄÏÉâ)Í¥ÄÎ¶¨
  // * Ïã†Í∑ú)  const handleSearch = () => { ÏóêÏÑú  const handleSearch = async () => { Î°ú async Ï∂îÍ∞Ä(await ÏÇ¨Ïö©ÏùÑ ÏúÑÌï¥)
  const handleSearch = async () => {
    const trimmedInput = inputValue.trim();
    if (trimmedInput === '') {
      setErrorMessage('Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî');
      return;
    }

    setErrorMessage('');
    setSearchQuery(trimmedInput);

    // Ï§ëÎ≥µ Ï†úÍ±∞ + ÏµúÎåÄ 5Í∞ú Ï†ÄÏû•
    setSearchHistory((prevHistory) => {
      const updated = [trimmedInput, ...prevHistory.filter(item => item !== trimmedInput)];
      localStorage.setItem('searchHistory', JSON.stringify(updated.slice(0, 5)));
      return updated.slice(0, 5);
    });

    // * Ïã†Í∑ú)ÏßÄÎèÑÍ≤ÄÏÉâ Í¥ÄÎ†®, ÏßÄÎèÑÍ≤ÄÏÉâÏùÑ Ïù¥Ïö©Ìï¥ÏÑú ÏøºÎ¶¨Í∞Ä Îì§Ïñ¥Ïò§Î©¥
    setLoading(true);

    // * Ïã†Í∑ú) ÎÑ§Ïù¥Î≤Ñ Í≤ÄÏÉâ APIÎ°ú Í≤ÄÏÉâÍ≤∞Í≥º Î∞õÏïÑÏò§Í∏∞
    try {
      const localResults = await naverLocalSearch(trimmedInput);

      // Í≤ÄÏÉâÍ≤∞Í≥ºÎ•º ÏßÄÏò§ÏΩîÎî©Ìï¥ÏÑú ÏúÑ/Í≤ΩÎèÑ Ï¢åÌëú Ï∂îÏ∂ú
      const placesWithCoords = await Promise.all(
        localResults.map(async item => {
          const coords = await naverGeocode(item.roadAddress || item.address);
          return { ...item, ...coords };
        })
      );

      // ÏµúÎåÄ 5Í∞úÏùò Í≤ÄÏÉâÍ≤∞Í≥º Ï∂úÎ†• (ÏßÄÏò§ÏΩîÎî©ÏúºÎ°ú Ï¢åÌëúÎ≥ÄÌôò Ïã§Ìå®Ìïú Îç∞Ïù¥ÌÑ∞Îäî Ï†úÏô∏ = Ïò§Î•òÏ†úÏô∏)
      setSearchResults(
        placesWithCoords
          .filter(item => item.lat && item.lng)
          .slice(0, 5)
      );
      setSelectedPlace(null);

    } catch (err) {
      setErrorMessage(`Í≤ÄÏÉâ Îêú Ïû•ÏÜåÍ∞Ä ÏóÜÏäµÎãàÎã§.`);
      setSearchResults([]);
    }
    setLoading(false);

    setInputValue('');
    setAutocompleteList([]);
  };

  // * Ïã†Í∑ú) ÏÉà Í∞ùÏ≤¥ÏÑ§Ï†ï: Í≤ÄÏÉâÏñ¥, Í≤ÄÏÉâÍ≤∞Í≥º, Í≤ÄÏÉâÍ≤∞Í≥º Î¶¨Ïä§Ìä∏, Ï¢åÌëúÍ∞í, ÏóêÎü¨Î©îÏÑ∏ÏßÄ
  const handleClear = () => {
    setInputValue('');
    setSearchQuery('');
    setSearchResults([]);
    setSelectedPlace(null);
    setAutocompleteList([]);
    setErrorMessage('');
  };

  // Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä/ÏÇ≠Ï†ú
  const toggleFavorite = (keyword) => {
    const updated = favorites.includes(keyword)
      ? favorites.filter(item => item !== keyword)
      : [...favorites, keyword];

    setFavorites(updated);
    localStorage.setItem('favorites', JSON.stringify(updated));
  };

  // ÌäπÏ†ï Í≤ÄÏÉâÏñ¥ ÏÇ≠Ï†ú
  const handleDeleteKeyword = (keyword) => {
    const updated = searchHistory.filter((item) => item !== keyword);
    setSearchHistory(updated);
    localStorage.setItem('searchHistory', JSON.stringify(updated));
  };

  // Ï†ÑÏ≤¥ Í≤ÄÏÉâ Í∏∞Î°ù ÏÇ≠Ï†ú
  const clearAllHistory = () => {
    setSearchHistory([]);
    localStorage.removeItem('searchHistory');
  };

  // ÏÇ¨Ïù¥ÎìúÎ∞î Ï†ëÍ∏∞/ÌéºÏπòÍ∏∞ ÌÜ†Í∏Ä
  const toggleSidebar = () => {
    setSidebarCollapsed(prev => !prev);
  };

  // ÏûêÎèôÏôÑÏÑ± ÌÅ¥Î¶≠ Ïãú Í≤ÄÏÉâ Ïã§Ìñâ
  const handleAutocompleteClick = (keyword) => {
    setInputValue(keyword);
    setSearchQuery(keyword);
    setAutocompleteList([]);
  };

  // Ïô∏Î∂Ä ÌÅ¥Î¶≠ Ïãú ÏûêÎèôÏôÑÏÑ± Îã´Í∏∞
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        autocompleteRef.current &&
        !autocompleteRef.current.contains(event.target)
      ) {
        setAutocompleteList([]);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // * Ïã†Í∑ú) Í≤ÄÏÉâ Îêú Ïû•ÏÜåÏóêÏÑú ÏùçÎ©¥Îèô Ï∂îÏ∂ú
  function extractRegion(addr) {
    if (!addr) return '';
    const parts = addr.split(' ');
    return parts.slice(0, 3).join(' ');
  }

  // Í∏∞Ï°¥Ïóê ÏûàÎçò sidebar Í∏∞Îä•
  return (
    <div className={`sidebar ${darkMode ? 'dark' : ''} ${sidebarCollapsed ? 'collapsed' : ''}`}>

      {/* ÏÇ¨Ïù¥ÎìúÎ∞î Ï†ëÍ∏∞ Î≤ÑÌäº */}
      <button
        className="collapse-button"
        onClick={toggleSidebar}
        aria-label={sidebarCollapsed ? 'ÏÇ¨Ïù¥ÎìúÎ∞î ÌéºÏπòÍ∏∞' : 'ÏÇ¨Ïù¥ÎìúÎ∞î Ï†ëÍ∏∞'}
      >
        {sidebarCollapsed ? '‚ñ∂' : '‚óÄ'}
      </button>

      {/* Ï†ëÌòÄÏûàÏßÄ ÏïäÏùÑ ÎïåÎßå ÎÇ¥Ïö© ÌëúÏãú */}
      {!sidebarCollapsed && (
        <>
          <h2>üõ†Ô∏è Í∏∞Îä• Î©îÎâ¥</h2>

          {/* ÏúÑÏπò Í≤ÄÏÉâ */}
          <h3>ÏúÑÏπò Í≤ÄÏÉâ</h3>
          <div className="input-wrapper" ref={autocompleteRef}>
            <input
              type="text"
              className="search-input"
              placeholder="ÎèÑÏãúÎÇò ÏßÄÏó≠ Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
              value={inputValue}
              onChange={handleInputChange}
              onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
              autoComplete="off"
              aria-label="ÏúÑÏπò Í≤ÄÏÉâ ÏûÖÎ†•Ï∞Ω"
              // * Ïã†Í∑ú) Í≤ÄÏÉâÍ≤∞Í≥º Î°úÎî©
              disabled={loading}
            />

            {/* ÏûÖÎ†• ÌÅ¥Î¶¨Ïñ¥ Î≤ÑÌäº */}
            {inputValue && (
              <button
                className="clear-input-btn"
                // const handleClear ÏïàÏóê Í∏∞Ï°¥Ïóê ÏûàÎçò ÏïÑÎûò Í∏∞Îä• Î™®Îëê Ìè¨Ìï®ÏúºÎ°ú onClick={() => handleClear()} Ìïú Ï§ÑÎ°ú ÏàòÏ†ï
                // setInputValue('');, setErrorMessage('');, setAutocompleteList([]);
                onClick={() => handleClear()}
                aria-label="Í≤ÄÏÉâÏñ¥ ÏßÄÏö∞Í∏∞"
              >
                ‚úï
              </button>
            )}

            {/* ÏûêÎèôÏôÑÏÑ± Î¶¨Ïä§Ìä∏ */}
            {autocompleteList.length > 0 && (
              <ul className="autocomplete-list" role="listbox">
                {autocompleteList.map((item, idx) => (
                  <li
                    key={idx}
                    role="option"
                    tabIndex={0}
                    className="autocomplete-item"
                    onClick={() => handleAutocompleteClick(item)}
                    onKeyDown={(e) => e.key === 'Enter' && handleAutocompleteClick(item)}
                  >
                    {item}{favorites.includes(item) ? ' ‚≠ê' : ''}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Í≤ÄÏÉâ Ïã§Ìñâ Î≤ÑÌäº */}
          {/* Ïã†Í∑ú) Ï∂îÍ∞Ä disabled={loading}, {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'üîç Í≤ÄÏÉâ'} */}
          <button className="search-button" onClick={handleSearch} disabled={loading}>
            {loading ? 'Í≤ÄÏÉâ Ï§ë...' : 'üîç Í≤ÄÏÉâ'}
            </button>

          {/* Ïò§Î•ò Î©îÏãúÏßÄ */}
          {errorMessage && <div className="error-message">{errorMessage}</div>}

          {/* ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ ÌÜ†Í∏Ä */}
          <button
            className="toggle-history-btn"
            onClick={() => setShowHistory(!showHistory)}
          >
            {showHistory ? 'ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ Ïà®Í∏∞Í∏∞ ‚ñ≤' : 'ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ Î≥¥Í∏∞ ‚ñº'}
          </button>

          {/* ÏµúÍ∑º Í≤ÄÏÉâÏñ¥ Î™©Î°ù */}
          {showHistory && searchHistory.length > 0 && (
            <>
              <h4>ÏµúÍ∑º Í≤ÄÏÉâÏñ¥</h4>
              <ul className="search-history">
                {searchHistory.map((item, index) => (
                  <li key={index} className="search-item">
                    <span
                      className="search-keyword"
                      onClick={() => {
                        setInputValue(item);
                        setSearchQuery(item);
                      }}
                    >
                      üîÅ {item}
                    </span>
                    <button
                      className="fav-btn"
                      onClick={() => toggleFavorite(item)}
                      aria-label={favorites.includes(item) ? 'Ï¶êÍ≤®Ï∞æÍ∏∞ Ìï¥Ï†ú' : 'Ï¶êÍ≤®Ï∞æÍ∏∞ Ï∂îÍ∞Ä'}
                    >
                      {favorites.includes(item) ? '‚òÖ' : '‚òÜ'}
                    </button>
                    <button
                      className="delete-btn"
                      onClick={() => handleDeleteKeyword(item)}
                      aria-label="Í≤ÄÏÉâÏñ¥ ÏÇ≠Ï†ú"
                    >
                      ‚ùå
                    </button>
                  </li>
                ))}
              </ul>

              {/* Ï†ÑÏ≤¥ Í≤ÄÏÉâ Í∏∞Î°ù ÏÇ≠Ï†ú Î≤ÑÌäº */}
              <button
                className="clear-history-btn"
                onClick={clearAllHistory}
              >
                üßπ Ï†ÑÏ≤¥ Í∏∞Î°ù ÏÇ≠Ï†ú
              </button>
            </>
          )}

          <hr />

          {/* Îã§ÌÅ¨Î™®Îìú & Ï¥àÍ∏∞Ìôî Î≤ÑÌäº (Í∞ÄÎ°ú Î∞∞Ïπò) */}
          <h3>ÏÑ§Ï†ï</h3>
          <div className="top-controls">
            <button
              className="toggle-darkmode-btn"
              onClick={() => setDarkMode(!darkMode)}
            >
              {darkMode ? 'üí° ÎùºÏù¥Ìä∏ Î™®Îìú' : 'üåô Îã§ÌÅ¨ Î™®Îìú'}
            </button>
            <button
              className="reset-page-btn"
              onClick={() => window.location.reload()}
            >
              üîÑ Ï¥àÍ∏∞Ìôî
            </button>
          </div>

          {/* Ïã†Í∑ú) Í≤ÄÏÉâÍ≤∞Í≥º 5Í∞ú ÎßµÏóê ÎßàÏª§ÌëúÏãú */}
          <div className="search-results">
            {searchResults && searchResults.length > 0 && searchResults.map((place, idx) => (
              <div
              key={idx}
              className="search-result-item"
              onClick={() => setSelectedPlace(place)}
              style={{ cursor: 'pointer' }}
              >
                <strong>{place.title}</strong>
                <span className="region">{extractRegion(place.roadAddress || place.address)}</span>
                <button
                onClick={e => {
                  e.stopPropagation();
                  navigator.clipboard.writeText(place.roadAddress || place.address);
                }}
                className="copy-btn"
                >Î≥µÏÇ¨</button>
                <div className="detail-addr">{place.roadAddress || place.address}</div>
                </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Sidebar;
